import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import ProductCard from '../components/ProductCard';
import './ProductsPage.css';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState([]);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProducts = async () => {
      console.log('ðŸ”„ Fetching products from:', 'http://localhost:7654/api/products');
      try {
        const response = await axios.get('http://localhost:7654/api/products', {
          params: {
            category: selectedCategory === 'All' ? undefined : selectedCategory,
            page: 1,
            limit: 50
          }
        });
        
        console.log('ðŸ“¦ API Response:', response.data);
        
        if (response.data.success) {
          const fetchedProducts = response.data.products;
          console.log('âœ… Products received:', fetchedProducts);
          setProducts(fetchedProducts);
          // Extract unique categories from products
          const uniqueCategories = ['All', ...new Set(fetchedProducts.map(p => p.category))];
          setCategories(uniqueCategories);
        }
      } catch (error) {
        console.error("âŒ Failed to fetch products:", error);
      }
      setLoading(false);
    };
    fetchProducts();
  }, [selectedCategory]);

  const handleAddToCart = (product) => {
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image
    });
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="products-page">
      <h1 className="page-title">All Products</h1>
      
      <div className="category-filters">
        {categories.map(category => (
          <button
            key={category}
            className={`filter-btn ${selectedCategory === category ? 'active' : ''}`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {loading ? (
        <p>Loading products...</p>
      ) : (
        <div className="product-grid">
          {products.map(product => (
            <ProductCard 
              key={product.id} 
              product={product} 
              onAddToCart={() => handleAddToCart(product)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
