import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './Chatbot.css';

const BACKEND_WEBHOOK_URL = 'http://localhost:3000/api/webhook';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const messagesEndRef = useRef(null);
  const recognitionRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    setMessages([
      {
        id: 1,
        text: 'Hello! Welcome to Meesho Shopping Assistant. How can I help you today?',
        sender: 'bot',
        timestamp: new Date(),
      },
    ]);

    // Initialize speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      setSpeechSupported(true);
      const recognition = new SpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setMessage(transcript);
        setTimeout(() => sendMessage(transcript), 500);
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognitionRef.current = recognition;
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const speakText = (text) => {
    if (voiceEnabled && 'speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.9;
      utterance.pitch = 1;
      utterance.volume = 0.8;
      
      // Try to use a female voice for the assistant
      const voices = speechSynthesis.getVoices();
      const femaleVoice = voices.find(voice => 
        voice.name.toLowerCase().includes('female') || 
        voice.name.toLowerCase().includes('samantha') ||
        voice.name.toLowerCase().includes('karen')
      );
      
      if (femaleVoice) {
        utterance.voice = femaleVoice;
      }

      speechSynthesis.speak(utterance);
    }
  };

  const sendMessage = async (textToSend = message) => {
    if (textToSend.trim() === '') return;

    const userMessage = {
      id: Date.now(),
      text: textToSend,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setMessage('');
    setLoading(true);

    try {
      const response = await axios.post(BACKEND_WEBHOOK_URL, {
        queryResult: { queryText: textToSend }
      });

      const botResponseData = response.data;
      let botMessageText = 'I am not sure how to respond to that.';

      if (botResponseData && botResponseData.fulfillmentText) {
        botMessageText = botResponseData.fulfillmentText;
      }

      const botMessage = {
        id: Date.now() + 1,
        text: botMessageText,
        sender: 'bot',
        timestamp: new Date(),
      };

      setMessages((prev) => [...prev, botMessage]);
      
      // Speak the bot response
      if (voiceEnabled) {
        setTimeout(() => speakText(botMessageText), 500);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Sorry, I could not connect to the server. Please make sure the backend is running.',
        sender: 'bot',
        timestamp: new Date(),
        isError: true,
      };
      setMessages((prev) => [...prev, errorMessage]);
    }

    setLoading(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  const quickReplies = ['Find products', 'Track my order', 'Get recommendations'];

  const handleQuickReply = (reply) => {
    sendMessage(reply);
  };

  const toggleVoiceRecognition = () => {
    if (!speechSupported) {
      alert('Speech recognition is not supported in your browser.');
      return;
    }

    if (isListening) {
      recognitionRef.current.stop();
    } else {
      recognitionRef.current.start();
    }
  };

  const toggleVoiceOutput = () => {
    setVoiceEnabled(!voiceEnabled);
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>üõçÔ∏è Shopping Assistant</h1>
        <div className="voice-controls">
          <button 
            className={`voice-toggle ${voiceEnabled ? 'active' : ''}`}
            onClick={toggleVoiceOutput}
            title="Toggle voice responses"
          >
            üîä
          </button>
        </div>
      </div>

      <div className="messages-container">
        {messages.map((msg) => (
          <div key={msg.id} className={`message ${msg.sender}`}>
            <div className={`message-content ${msg.isError ? 'error' : ''}`}>
              {msg.text}
              {msg.sender === 'bot' && !msg.isError && (
                <button 
                  className="speak-button"
                  onClick={() => speakText(msg.text)}
                  title="Speak this message"
                >
                  üîä
                </button>
              )}
            </div>
          </div>
        ))}

        {loading && (
          <div className="message bot">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {messages.length > 0 && messages[messages.length - 1].sender === 'bot' && !loading && (
        <div className="quick-replies">
          {quickReplies.map((reply, index) => (
            <button 
              key={index}
              onClick={() => handleQuickReply(reply)}
              className="quick-reply-btn"
            >
              {reply}
            </button>
          ))}
        </div>
      )}

      <div className="input-container">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder={isListening ? "Listening..." : "Type your message..."}
          disabled={loading || isListening}
        />
        {speechSupported && (
          <button 
            className={`mic-button-input ${isListening ? 'listening' : ''}`}
            onClick={toggleVoiceRecognition}
            title={isListening ? 'Stop listening' : 'Start voice input'}
          >
            {isListening ? 'üî¥' : 'üé§'}
          </button>
        )}
        <button className="send-button" onClick={() => sendMessage()} disabled={loading || !message.trim() || isListening}>
          {loading ? '...' : 'Send'}
        </button>
      </div>

      {isListening && (
        <div className="listening-indicator">
          <div className="sound-wave">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
          </div>
          <span>Listening...</span>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
