import React, { useState, useEffect, useRef } from 'react';
import { 
  Container, 
  Paper, 
  Box, 
  Typography, 
  IconButton,
  Fade,
  Alert
} from '@mui/material';
import { Mic, MicOff, VolumeUp } from '@mui/icons-material';
import MessageInput from './MessageInput';
import ProductCard from './ProductCard';
import QuickReplies from './QuickReplies';
import VoiceToggle from './VoiceToggle';
import { useVoiceRecognition } from '../hooks/useVoiceRecognition';
import apiService from '../services/apiService';
import './ChatWindow.css';

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [sessionId] = useState(`user_${Math.random().toString(36).substr(2, 9)}`);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  
  const {
    isListening,
    transcript,
    startListening,
    stopListening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useVoiceRecognition();

  useEffect(() => {
    // Initialize with welcome message
    const welcomeMessage = {
      id: Date.now(),
      text: "Hello! Welcome to Meesho! I'm your shopping assistant. I can help you find products, track orders, and get personalized recommendations. How can I help you today?",
      sender: 'bot',
      timestamp: new Date(),
      quickReplies: ['Find products', 'Track my order', 'Get recommendations', 'Customer support']
    };
    setMessages([welcomeMessage]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (transcript) {
      sendMessage(transcript);
      resetTranscript();
    }
  }, [transcript]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const sendMessage = async (text) => {
    if (!text.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: text.trim(),
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsTyping(true);
    setError(null);

    try {
      const response = await apiService.sendMessage({
        queryResult: {
          queryText: text,
          intent: { displayName: 'user.message' },
          parameters: {}
        },
        session: `projects/demo-project/agent/sessions/${sessionId}`
      });

      setIsTyping(false);

      const botMessage = {
        id: Date.now() + 1,
        text: response.fulfillmentText,
        sender: 'bot',
        timestamp: new Date(),
        fulfillmentMessages: response.fulfillmentMessages
      };

      setMessages(prev => [...prev, botMessage]);

    } catch (error) {
      setIsTyping(false);
      setError('Sorry, I\'m having trouble connecting. Please try again.');
      
      const errorMessage = {
        id: Date.now() + 1,
        text: 'I apologize, but I\'m experiencing technical difficulties. Please try again in a moment.',
        sender: 'bot',
        timestamp: new Date(),
        isError: true
      };

      setMessages(prev => [...prev, errorMessage]);
    }
  };

  const handleQuickReply = (reply) => {
    sendMessage(reply);
  };

  const handleVoiceToggle = () => {
    if (isListening) {
      stopListening();
    } else {
      startListening();
    }
  };

  const speakText = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.9;
      utterance.pitch = 1;
      speechSynthesis.speak(utterance);
    }
  };

  const renderMessage = (message) => {
    const { id, text, sender, fulfillmentMessages, quickReplies, isError } = message;

    return (
      <Fade in={true} key={id}>
        <Box className={`message ${sender}`}>
          <Box className="message-content">
            <Typography 
              variant="body2" 
              className={`message-text ${isError ? 'error' : ''}`}
            >
              {text}
            </Typography>
            
            {sender === 'bot' && (
              <IconButton 
                size="small" 
                onClick={() => speakText(text)}
                className="speak-button"
              >
                <VolumeUp fontSize="small" />
              </IconButton>
            )}
          </Box>

          {/* Render rich messages (product cards) */}
          {fulfillmentMessages && fulfillmentMessages.map((msg, index) => (
            <Box key={index} className="rich-message">
              {msg.card && <ProductCard card={msg.card} onAction={sendMessage} />}
            </Box>
          ))}

          {/* Render quick replies */}
          {quickReplies && (
            <QuickReplies 
              replies={quickReplies} 
              onReplyClick={handleQuickReply} 
            />
          )}
        </Box>
      </Fade>
    );
  };

  return (
    <Container maxWidth="sm" className="chat-container">
      <Paper elevation={3} className="chat-window">
        {/* Header */}
        <Box className="chat-header">
          <Typography variant="h5" className="header-title">
            üõçÔ∏è Meesho Assistant
          </Typography>
          <Typography variant="body2" className="header-subtitle">
            Your AI shopping companion
          </Typography>
          
          {browserSupportsSpeechRecognition && (
            <VoiceToggle 
              isListening={isListening}
              onToggle={handleVoiceToggle}
            />
          )}
        </Box>

        {/* Error Alert */}
        {error && (
          <Alert severity="error" onClose={() => setError(null)}>
            {error}
          </Alert>
        )}

        {/* Messages */}
        <Box className="messages-container">
          {messages.map(renderMessage)}
          
          {/* Typing indicator */}
          {isTyping && (
            <Box className="message bot">
              <Box className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </Box>
            </Box>
          )}
          
          <div ref={messagesEndRef} />
        </Box>

        {/* Input */}
        <MessageInput 
          onSendMessage={sendMessage}
          isListening={isListening}
          transcript={transcript}
        />
      </Paper>
    </Container>
  );
};

export default ChatWindow;
