import React, { useState } from 'react';
import './ProductCard.css';

const ProductCard = ({ product, onAddToCart, compact = false }) => {
  const [isAdded, setIsAdded] = useState(false);

  const handleAddToCart = () => {
    onAddToCart(product);
    setIsAdded(true);
    
    // Reset after 2 seconds
    setTimeout(() => {
      setIsAdded(false);
    }, 2000);
  };

  return (
    <div className={`product-card-chat ${compact ? 'compact' : ''}`}>
      <img 
        src={product.image} 
        alt={product.name} 
        className="product-image-chat"
        onError={(e) => {
          e.target.src = 'https://via.placeholder.com/150x150?text=No+Image';
        }}
      />
      <div className="product-details-chat">
        <h4 className="product-name-chat">{product.name}</h4>
        <p className="product-category-chat">{product.category}</p>
        <div className="product-price-chat">₹{product.price}</div>
        {product.rating > 0 && (
          <div className="product-rating-chat">
            ⭐ {product.rating}
          </div>
        )}
        <button 
          onClick={handleAddToCart}
          className={`add-to-cart-btn-chat ${isAdded ? 'added' : ''}`}
          disabled={!product.inStock}
        >
          {isAdded 
            ? '✓ Added' 
            : product.inStock 
              ? 'Add to Cart' 
              : 'Out of Stock'
          }
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
