import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import VoiceToggle from './VoiceToggle';
import ProductCard from './ProductCard';
import './ChatWindow.css';

const ChatWindow = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "üëã Hi! I'm your Meesho shopping assistant. I can help you track orders, find products, and get recommendations. Try saying 'Show me dresses' or 'Track my order'!",
      sender: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isVoiceMode, setIsVoiceMode] = useState(false);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();
  const { addToCart } = useCart();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (messageText) => {
    if (!messageText.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);

    try {
      // Send to our enhanced webhook
      const response = await axios.post('http://localhost:7654/api/webhook', {
        queryResult: {
          queryText: messageText
        }
      });

      const botMessage = {
        id: Date.now() + 1,
        text: response.data.fulfillmentText,
        sender: 'bot',
        timestamp: new Date(),
        products: response.data.products || null,
        mlAnalysis: response.data.mlAnalysis || null // Add ML analysis data
      };

      setMessages(prev => [...prev, botMessage]);

      // Text-to-speech for voice mode
      if (isVoiceMode && 'speechSynthesis' in window) {
        const utterance = new SpeechSynthesisUtterance(response.data.fulfillmentText);
        utterance.rate = 0.8;
        utterance.pitch = 1;
        window.speechSynthesis.speak(utterance);
      }

    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: "Sorry, I'm having trouble connecting. Please try again.",
        sender: 'bot',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddToCart = (product) => {
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image
    });
    
    // Add confirmation message
    const confirmMessage = {
      id: Date.now(),
      text: `‚úÖ Added "${product.name}" to your cart! You can continue shopping or go to cart to checkout.`,
      sender: 'bot',
      timestamp: new Date()
    };
    setMessages(prev => [...prev, confirmMessage]);
  };

  const handleViewAllProducts = (searchTerm) => {
    // Navigate to products page with search filter
    navigate(`/products?search=${encodeURIComponent(searchTerm)}`);
  };

  const handleSend = () => {
    sendMessage(inputText);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleVoiceResult = (transcript) => {
    console.log('Voice input received:', transcript);
    sendMessage(transcript);
  };

  const quickActions = [
    { text: "Show me dresses", action: () => sendMessage("Show me dresses") },
    { text: "Find phones", action: () => sendMessage("Find phones under 20000") },
    { text: "Track order", action: () => sendMessage("Track my order") },
    { text: "Help", action: () => sendMessage("What can you do?") }
  ];

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h3>üõçÔ∏è Meesho Assistant</h3>
        <div className="voice-indicator">
          {isVoiceMode && <span className="voice-active">üéôÔ∏è Voice Active</span>}
        </div>
      </div>

      <div className="messages-container">
        {messages.map(message => (
          <div key={message.id} className={`message ${message.sender}`}>
            <div className="message-content">
              <div className="message-text">{message.text}</div>
              
              {/* ML Analysis Indicator */}
              {message.mlAnalysis && (
                <div className="ml-analysis">
                  <small>
                    Flow: {message.mlAnalysis.flow} | 
                    Sentiment: {message.mlAnalysis.sentiment} | 
                    Intent: {message.mlAnalysis.intent}
                  </small>
                </div>
              )}
              
              {/* Product Cards Display */}
              {message.products && message.products.length > 0 && (
                <div className="products-grid-chat">
                  {message.products.map(product => (
                    <ProductCard
                      key={product.id}
                      product={product}
                      onAddToCart={handleAddToCart}
                      compact={true}
                    />
                  ))}
                  <button 
                    className="view-all-products-btn"
                    onClick={() => handleViewAllProducts(message.text)}
                  >
                    View All Products ‚Üí
                  </button>
                </div>
              )}
              
              <div className="message-time">
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        
        {isLoading && (
          <div className="message bot">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      <div className="quick-actions">
        {quickActions.map((action, index) => (
          <button
            key={index}
            onClick={action.action}
            className="quick-action-btn"
            disabled={isLoading}
          >
            {action.text}
          </button>
        ))}
      </div>

      <div className="chat-input">
        <div className="input-row">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type a message or try 'Show me dresses'..."
            disabled={isLoading}
            className="chat-input-field"
          />
          <button 
            onClick={handleSend} 
            disabled={isLoading || !inputText.trim()}
            className="send-btn"
          >
            Send
          </button>
        </div>
        
        <VoiceToggle 
          onVoiceResult={handleVoiceResult}
          onVoiceModeChange={setIsVoiceMode}
          disabled={isLoading}
        />
      </div>
    </div>
  );
};

export default ChatWindow;
