import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import './ProductsPage.css';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [showSearchOptions, setShowSearchOptions] = useState(false);
  const [addedItems, setAddedItems] = useState(new Set()); // Track added items
  const { addToCart } = useCart();

  const categories = [
    'All', 'Fashion', 'Electronics', 'Home & Kitchen', 'Beauty & Personal Care', 
    'Books', 'Sports & Fitness', 'Toys & Games', 'Groceries', 'Automotive', 
    'Health & Wellness', 'Jewelry', 'Bags & Luggage', 'Pet Supplies', 
    'Office Products', 'Musical Instruments'
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    filterProducts();
  }, [products, searchQuery, selectedCategory]);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:7654/api/products');
      if (response.data.success) {
        setProducts(response.data.products);
        setFilteredProducts(response.data.products);
      }
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const filterProducts = () => {
    let filtered = products;

    // Filter by search query
    if (searchQuery.trim()) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    setFilteredProducts(filtered);
  };

  const handleSearch = () => {
    setShowSearchOptions(!showSearchOptions);
    filterProducts();
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setShowSearchOptions(false);
  };

  const handleAddToCart = (product) => {
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image
    });
    
    // Add visual feedback without popup
    setAddedItems(prev => new Set([...prev, product.id]));
    
    // Remove the visual feedback after 2 seconds
    setTimeout(() => {
      setAddedItems(prev => {
        const newSet = new Set(prev);
        newSet.delete(product.id);
        return newSet;
      });
    }, 2000);
  };

  if (loading) {
    return (
      <div className="products-page">
        <div className="loading">Loading products...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="products-page">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="products-page">
      <div className="products-header">
        <h1>All Products</h1>
        <p>Discover amazing deals and quality products</p>
      </div>

      {/* Search Section */}
      <div className="search-section">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search for products, categories..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          <button onClick={handleSearch} className="search-btn">
            🔍 Search
          </button>
        </div>

        {/* Selected Category Display */}
        {selectedCategory !== 'All' && (
          <div className="selected-category">
            <span>Category: {selectedCategory}</span>
            <button onClick={() => setSelectedCategory('All')} className="clear-filter">
              ✕ Clear
            </button>
          </div>
        )}

        {/* Search Options Dropdown */}
        {showSearchOptions && (
          <div className="search-options">
            <h4>Filter by Category:</h4>
            <div className="category-grid">
              {categories.map(category => (
                <button
                  key={category}
                  onClick={() => handleCategorySelect(category)}
                  className={`category-option ${selectedCategory === category ? 'active' : ''}`}
                >
                  {category}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Results Info */}
      <div className="results-info">
        <p>Showing {filteredProducts.length} products</p>
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-category">{product.category}</p>
              <div className="product-price">₹{product.price}</div>
              {product.rating && (
                <div className="product-rating">
                  ⭐ {product.rating}
                </div>
              )}
              <button 
                onClick={() => handleAddToCart(product)}
                className={`add-to-cart-btn ${addedItems.has(product.id) ? 'added' : ''}`}
                disabled={!product.inStock}
              >
                {addedItems.has(product.id) 
                  ? '✓ Added to Cart' 
                  : product.inStock 
                    ? 'Add to Cart' 
                    : 'Out of Stock'
                }
              </button>
            </div>
          </div>
        ))}
      </div>

      {filteredProducts.length === 0 && (
        <div className="no-results">
          <h3>No products found</h3>
          <p>Try adjusting your search or filter criteria</p>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
