import React from 'react';
import { useCart } from '../context/CartContext';
import {
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  Box,
  Chip,
  Rating
} from '@mui/material';
import { ShoppingCart, Visibility } from '@mui/icons-material';
import './ProductCard.css';

const ProductCard = ({ card, onAction }) => {
  const { addToCart } = useCart();

  const handleAction = (postback) => {
    if (postback.startsWith('product_details_')) {
      const productId = postback.replace('product_details_', '');
      onAction(`Show me details for product ${productId}`);
    } else if (postback.startsWith('add_to_cart_')) {
      const productId = postback.replace('add_to_cart_', '');
      onAction(`Add product ${productId} to cart`);
    }
  };

  // Parse price and rating from subtitle
  const parseSubtitle = (subtitle) => {
    const parts = subtitle.split(' - ');
    const price = parts[0];
    const brand = parts[1] || '';
    return { price, brand };
  };

  const { price, brand } = parseSubtitle(card.subtitle || '');

  return (
    <Card 
      sx={{ 
        maxWidth: 300, 
        margin: '8px 0',
        borderRadius: 2,
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        transition: 'transform 0.2s',
        '&:hover': {
          transform: 'translateY(-2px)',
          boxShadow: '0 4px 16px rgba(0,0,0,0.15)'
        }
      }}
    >
      {card.imageUri && (
        <CardMedia
          component="img"
          height="180"
          image={card.imageUri}
          alt={card.title}
          sx={{ objectFit: 'cover' }}
        />
      )}
      
      <CardContent sx={{ pb: 1 }}>
        <Typography 
          variant="h6" 
          component="h3"
          sx={{ 
            fontWeight: 600,
            fontSize: '1rem',
            lineHeight: 1.3,
            mb: 1
          }}
        >
          {card.title}
        </Typography>
        
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
          <Typography 
            variant="h6" 
            color="primary"
            sx={{ fontWeight: 700 }}
          >
            {price}
          </Typography>
          {brand && (
            <Chip 
              label={brand} 
              size="small" 
              variant="outlined"
              sx={{ fontSize: '0.7rem' }}
            />
          )}
        </Box>

        {/* Mock rating */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Rating value={4.2} precision={0.1} size="small" readOnly />
          <Typography variant="caption" color="text.secondary">
            (4.2)
          </Typography>
        </Box>
      </CardContent>

      <CardActions sx={{ pt: 0, px: 2, pb: 2 }}>
        {card.buttons?.map((button, index) => (
          <Button
            key={index}
            variant={button.text.includes('Add') ? 'contained' : 'outlined'}
            size="small"
            startIcon={
              button.text.includes('Add') ? <ShoppingCart /> : <Visibility />
            }
            onClick={() => handleAction(button.postback)}
            sx={{ 
              borderRadius: 20,
              textTransform: 'none',
              fontSize: '0.8rem'
            }}
          >
            {button.text}
          </Button>
        ))}
      </CardActions>
    </Card>
  );
};

export default ProductCard;
